import React from 'react'
import SignOut from './SignOut.js'
import {useState, useEffect, useRef} from 'react';
import {auth, db } from '../firebase.js';
import SendMessage from './SendMessage.js';
import defaultIcon from '../googleuser.png'








function Chat() {
    const scroll = useRef()
    const [messages,setMessages] = useState([])
    const [ppChecker, setPPChecker] = useState(true)


    useEffect(()=>{
        db.collection('messages').orderBy('createdAt').limit(70).onSnapshot(snapshot => {
            setMessages(snapshot.docs.map(doc => doc.data()))
        })
    },[])

  return (
    <div>
            <SignOut/>

         <div className="msgs">

                {messages.map(({ id, text, photoURL, uid, displayName, defaultIcon }) => (

                    <div>                               
                        <div key={id} className={`msg ${auth.currentUser.uid === uid ? 'sent' : 'received'}`}>
                            <img src={ppChecker ? {photoURL} : ''} alt="" />
                            <p>{displayName} : {text}</p>
                        </div>
                    </div>
                ))}
            </div>


        <SendMessage scroll = {scroll}/>
         <div ref={scroll}></div>
    </div>


  )
}

export default Chat
