/* Firebase V9 importları */
import firebase from 'firebase/compat/app';
import {getAuth} from 'firebase/compat/auth';            
import 'firebase/compat/firestore'; 

/* Firebase bağlantısı için kimlik bilgileri */
const firebaseApp = firebase.initializeApp({  
    apiKey: "AIzaSyDzoOudcSadfkOOBYMx8slz-VWoUuqC9Io",
    authDomain: "chatappwithreact-1d39e.firebaseapp.com",
    projectId: "chatappwithreact-1d39e",
    storageBucket: "chatappwithreact-1d39e.appspot.com",
    messagingSenderId: "868192725839",
    appId: "1:868192725839:web:fbf9300a669493bf64f33a"
})

/* Firebase databasesi olan Firestore ile bağlantı */
const db = firebaseApp.firestore()  

/* Firebase kimlik doğrulaması için auth bağlantısı */
const auth = getAuth(firebaseApp)  

/* database ve autha erişim için exportlar */
export  { db,auth }
