import React from 'react'
import Button from '@mui/material/Button';
import firebase from 'firebase/compat/app';
import {auth} from '../firebase.js'

function SignIn() {

  /* Google ile giriş için kullanılacak olan auth */
    function signInWithGoogle() {

        const provider = new firebase.auth.GoogleAuthProvider()
        auth.signInWithPopup(provider)
    }

  return (

    /* Google ile giriş için kullanılacak butonun yapısı ve tasarımı */
    <div style={{ display: 'flex', justifyContent: 'center', height: '100vh', alignItems: 'center' }} >
        <Button style={{ padding: '30px', fontSize: '20px', borderRadius: '0', fontWeight: '600' }} onClick={signInWithGoogle}>GOOGLE ILE GIRIS YAP</Button>
    </div>
  )
}

export default SignIn
