import React from 'react'
import Button from '@mui/material/Button';
import Input from '@mui/material/Input';
import firebase from 'firebase/compat/app';
import {auth} from '../firebase.js'
import './styles/SignIn.css'

function SignIn() {

  /* Google ile giriş için kullanılacak olan auth ve Firebase ile gelen provider fonksiyonu */
    function signInWithGoogle() {

        const provider = new firebase.auth.GoogleAuthProvider()
        auth.signInWithPopup(provider)
    }



  return (

    /* Google ile giriş için kullanılacak butonun yapısı ve tasarımı */
    <div style={{ display: 'flex', justifyContent: 'center', height: '100vh', alignItems: 'center', flexDirection: 'column' }} >
      <div className = 'email-container'>
            <Input className='email' type = 'e-mail' placeholder = 'e-mail adresinizi giriniz:'></Input>
            <Input type = 'sifre' placeholder = 'sifrenizi giriniz:'></Input>


      </div>
        <Button style={{ fontSize: '15px', borderRadius: '0', fontWeight: '500', color: 'red' }} onClick={signInWithGoogle}>GOOGLE ILE GIRIS YAP</Button>
    </div>
  )
}

export default SignIn
